import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ApiServiceService } from '../services/api-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-forget',
  templateUrl: './forget.component.html',
  styleUrls: ['./forget.component.css']
})
export class ForgetComponent {

  constructor(private api:ApiServiceService, private router:Router) { }

  Forget = new FormGroup({
    email: new FormControl('',[Validators.required]),
    password: new FormControl('',[Validators.required, Validators.pattern(/^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]+$/)]),
    confirmpassword: new FormControl('',[Validators.required, Validators.pattern(/^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]+$/)])
  })

  exist_error=false;

  email_error=false;
  password_error=false;
  confirm_error=false;

  // Check(){
  //   this.email_error=this.Forget.get('email')?.valid ? false : true
  // }

  Email_error(){
    this.email_error=false;
  }
  Password_error(){
    this.password_error=false;
  }
  Confirm_error(){
    this.confirm_error=false;
  }

  domain="@bengalintituteoftechnology.online"

  Submit(){
    if(this.Forget.valid){
      this.api.Email({"email":this.Forget.get('email')?.value+this.domain}).subscribe(
        response=>{
          if(response.body.data['successful'] === 'true'){
            const body={"email":this.Forget.get("email")?.value+this.domain,"password":this.Forget.get("password")?.value}
            this.api.Forget(body).subscribe(
              resp=>{
                this.api.otptoken=resp.headers.get('otpverify')
                this.router.navigate(['/otp'])
              }
            )
          }else{
            this.exist_error=true;
          }
        },error=>{
          console.log(error)
      })
    }else{
      this.email_error=this.Forget.get('email')?.valid ? false : true;
      this.password_error=this.Forget.get('password')?.valid ? false : true;
      this.confirm_error=this.Forget.get('confirm')?.valid ? false : true;
    }
  }
}
<div class="body">
  <div class="main">
    <h1 class="h1">Forget Password</h1>
    <hr />
    <form [formGroup]="Forget" (ngSubmit)="Submit()" class="inputs">
      <span class="error email_error" *ngIf="exist_error">
        please enter a valid email</span
      >
      <span
        class="error email_error"
        *ngIf="
          (Forget.get('email')?.invalid && Forget.get('email')?.touched) ||
          email_error
        "
      >
        please enter a valid email</span
      >
      <p>
        Enter email&nbsp;
        <input
          type="email"
          formControlName="email"
          [class.buttom_color]="
            (Forget.get('email')?.invalid && Forget.get('email')?.touched) ||
            email_error
          "
          (focus)="Email_error()"
          class="email"
        />{{ domain }}
      </p>
      <span
        class="error password_error"
        *ngIf="
          (Forget.get('password')?.invalid &&
            Forget.get('password')?.touched) ||
          password_error
        "
        >Enter password</span
      >
      <p>
        Forget Password &nbsp;&nbsp;&nbsp;&nbsp;<input
          type="password"
          formControlName="password"
          [class.buttom_color]="
            (Forget.get('password')?.invalid &&
              Forget.get('password')?.touched) ||
            password_error
          "
          (focus)="Password_error()"
          class="password"
        />
      </p>
      <span
        class="error reenter_password"
        *ngIf="
          (Forget.get('confirmpassword')?.invalid &&
            Forget.get('confirmpassword')?.touched) ||
          confirm_error
        "
        >Re Enter password</span
      >
      <p>
        Confirm Password &nbsp;<input
          type="password"
          formControlName="confirmpassword"
          [class.buttom_color]="
            (Forget.get('confirmpassword')?.invalid &&
              Forget.get('confirmpassword')?.touched) ||
            confirm_error
          "
          (focus)="Confirm_error()"
          class="confirm"
        />
      </p>
      <div class="forget">
        <button class="forget_button" type="submit">Forget</button>
      </div>
    </form>
    <div class="other">
      <a routerLink="/login">Back To Login</a>
    </div>
  </div>
</div>
