import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ApiServiceService } from '../services/api-service.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

  constructor(private router: Router, private api: ApiServiceService){
    this.url=router.url
    this.display=api.sidebar
  }
  url:string=""

  display:boolean;

  ChangeSidebar(){
    this.api.sidebar=!this.api.sidebar
    this.display=this.api.sidebar
  }

  Inboxurl(){
    return this.url==="/inbox"
  }

  Starredurl(){
    return this.url==="/starred"
  }

  Senturl(){
    return this.url==="/sent"
  }

  Drafturl(){
    return this.url==="/draft"
  }

  Spamurl(){
    return this.url==="/spam"
  }

  Trashurl(){
    return this.url==="/trash"
  }

}
