import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

  display:boolean=true;

  ChangeSidebar(){
    this.display=!this.display;
  }

}
